package project1;

public class compound_simple_interest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 System.out.print("Principal = 9000: ");
		    double principal = 9000;

		    System.out.println("rate of interest = 10 : ");
		    double rate = 10;

		    System.out.println("number of years = 5 : ");
		    int years = 5;

		    double simpleInterest = (principal * rate * years) / 100;
		    System.out.println("Simple Interest: " + simpleInterest);
		  }
		

	}


